     */
    public static EntryInfo createTree() {
        return EntryInfo.builder()
                .isWall(true)
                .isFallthrough(false)
                .isTransparent(false)
                .character('♠')
                .name("Tree")
                .colorLight(AnsiColor.BRIGHT_GREEN)
                .colorDark(AnsiColor.GREEN)
